import React from "react";
import './style.css'
import cloud from './cloud.png'
import rain from './rain.png'
import sunny from './sun.png'
import './imageStyle.css'

const method = {method : "GET"};

class Weather extends React.Component
{
    constructor() {
        super();
        this.state = {
            data : {"content" : "none"},
            image : null
        }
        this.fetchData = this.fetchData.bind(this);
    }

    async fetchData()
    {
        const response = await fetch("http://localhost:8000/current");
        const jsonData = await response.json();
        this.setState({data : jsonData});
    }

    componentDidMount() {
        this.fetchData()
            .then(() => {
                console.log("Done with fetch");
                switch (this.state.data.weather[0].main)
                {
                    case "Clouds": case "Mist" : this.setState({image : cloud});break;
                    case "Rain": case "Drizzle": this.setState({image : rain});break;
                    case "Clear" : this.setState({image : sunny});break;
                }
            });
    }

    render()
    {
        return(
            <section id={"weatherCard"}>
                <h1>Current Weather</h1>
                <img alt={"image"} src={this.state.image}/>
                {/*<h2>{this.state.data.weather[0].description}</h2>*/}
            </section>
        )
    }
}

export default Weather
